mapper.update:
update review_upload
	set review_filename = #{review_filename}
		review_filepath = #{review_filepath}
		review_uuid = #{review_uuid}
	where review_upload_no = #{review_upload_no}

mapper.select:
SELECT max(REVIEW_UPLOAD_NO)
AS REVIEW_UPLOAD_NO FROM REVIEW_UPLOAD
WHERE REVIEW_NO = #{review_no}

SELECT max(REVIEW_CATEGORY_NO)
AS REVIEW_CATEGORY_NO FROM REVIEW_CATEGORY
WHERE REVIEW_NO = #{review_no}
